var faceon=function(){$(".tool_box").toggle()};function put(){var value=$('input[name="upload"]').val();var arr=value.split(".");if(arr[1]=="jpg"||arr[1]=="gif"||arr[1]=="png"){$("#picture").ajaxSubmit({url:YMWL_ROOT_URL+"/admin/set/upload",type:"post",success:function(res){var str="";str+="<img   src='"+res+"'>";$("#text_in").append(str)}})}else{layer.msg("请选择图片",{icon:2})}}function getbig(obj){var text=$(obj).attr("src");layer.open({type:1,title:false,closeBtn:0,area:["600px","400px"],skin:"layui-layer-nobg",shadeClose:true,content:"<img src='"+text+"' width='100%' height='100%'>"})}function get(channel){$.ajax({url:YMWL_ROOT_URL+"/admin/set/get",type:"post",data:{channel:channel},success:function(res){if(res){console.log(res)}getchat();getwait()}})}function getwait(){$.ajax({url:YMWL_ROOT_URL+"/admin/set/getwait",success:function(res){if(res){$(".wait_list").empty();var data=$.parseJSON(res);var a="";$.each(data,function(k,v){a+="<li class='cu_list'>"+v.visiter+" <span>访问地址："+v.domain+"</span>";a+="<button class='am-btn am-btn-primary am-btn-xs' onclick='get("+v.channel+")' style='position: absolute;right: 10px;'>认领</button>";a+="</li>"});$(".wait_list").append(a)}else{$(".wait_list").empty()}}})}function getchat(){$.ajax({url:YMWL_ROOT_URL+"/admin/set/getchats",success:function(res){if(res){$("#chat_list").empty();$(".chatlist").removeClass("hide");$(".chatbox").css("width","75%");var sdata=$.cookie("cu_com");if(sdata){var json=$.parseJSON(sdata);var debug=json.channel}else{var debug=""}var data=$.parseJSON(res);var a="";$.each(data,function(k,v){var str=JSON.stringify(v);if(debug==v.channel){a+="<li style='border:2px solid #E62246;'><a class='cu_chat' href='javascript:choose("+str+");' >"+v.visiter+"</a>";a+="<i class='close' onclick='cut("+v.channel+")' style='position: absolute;right: 5px;top:8px;'></i>";a+="</li>"}else{a+="<li class='wait_chat' ><a class='cu_chat' href='javascript:choose("+str+");' >"+v.visiter+"</a>";a+="<i class='close' onclick='cut("+v.channel+")' style='position: absolute;right: 5px;top:8px;'></i>";a+="<sapn class='c"+v.channel+" am-circle' style='color:#FFFFFF;position:absolute;left:140px;width:16px;background: #D92F2F;text-align:center;'></span></li>";getnews(v.channel)}});$("#chat_list").append(a)}else{$(".chatbox").css("width","100%");$(".chatlist").addClass("hide");$("#chat_list").empty();$.cookie("cu_com","")}}})}function getblacklist(){$.ajax({url:YMWL_ROOT_URL+"/admin/set/getblackdata",success:function(res){if(res){$(".black_list").empty();var data=$.parseJSON(res);var a="";$.each(data,function(k,v){a+="<li class='cu_list'>"+v.visiter;a+="<button class='am-btn am-btn-danger am-btn-xs' onclick='cut("+v.channel+")' style='position: absolute;right: 10px;'>删除</button>";a+="</li>"});$(".black_list").append(a)}else{$(".black_list").empty()}}})}function getwatch(cha){$.ajax({url:YMWL_ROOT_URL+"/admin/set/getwatch",type:"post",data:{channel:cha},success:function(res){console.log(res)}})}function getnews(cha){$.ajax({url:YMWL_ROOT_URL+"/admin/set/getmessage",type:"post",data:{channel:cha},success:function(res){if(res){var data=$.parseJSON(res);$.each(data,function(k,v){if(v.number==0){$(".c"+v.channel).addClass("hide")}else{$(".c"+v.channel).text(v.number)}})}}})}var send=function(){var msg=$("#text_in").html();var sid=$("#channel").text();var se=$("#chatmsg_submit").attr("name");var myDate=new Date();var time=myDate.toLocaleTimeString();var customer=$(".visiter").text();var str="";str+='<li class="chatmsg"><div class="showtime">'+time+"</div>";str+="<div class='outer-right'><div class='service'>";str+="<pre>"+msg+"</pre>";str+="</div></div>";str+="</li>";$(".conversation").append(str);$("#text_in").empty();var div=document.getElementById("wrap");div.scrollTop=div.scrollHeight;$.ajax({url:YMWL_ROOT_URL+"/admin/set/chats",type:"post",data:{froms:se,tos:customer,content:msg,channel:sid},success:function(res){console.log(res)}})};